import React, { useEffect } from 'react';
import { Component, useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="/home">
        MyBookShelf
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
  const classes = useStyles();

  //email形式チェック正規表現
  const emailReg = /^[A-Za-z0-9]{1}[A-Za-z0-9_.-]*@{1}[A-Za-z0-9_.-]{1,}\.[A-Za-z0-9]{1,}$/;
  //password形式チェック正規表現（英数字８〜２０文字）
  const passwordReg = /^(?=.*?[a-z])(?=.*?\d)[a-z\d]{8,20}$/i;

  //API通信メッセージ
  const[apiMsg, setApiMsg] = useState("");

  //Email関連のステート
  const[email, setEmail] = useState("");
  const[emailMsg, setEmailMsg] = useState("Email Address");
  const[emailCheckFlg, setEmailCheckFlg] = useState(false);

  //password関連のステート
  const[password, setPassword] = useState("");
  const[passwordMsg, setPasswordMsg] = useState("Password");
  const[passwordCheckFlg, setPasswordCheckFlg] = useState(false);

  //SubmitButton非活性処理
  const[disabledFlg, setDisabledFlg] = useState("false");
  useEffect(()=>{
    passwordCheckFlg && emailCheckFlg ? setDisabledFlg("") : setDisabledFlg("false");
  });

  let emailOnChange = (e) =>{
    let email = e.target.value;
    //空文字・null・５０文字以内・email形式のバリデーションチェック
    if(email === "" || email === null){
        setEmailMsg("Email Address");
        setEmailCheckFlg(false);
    }else if(email.length <= 50 && emailReg.test(email)){
        setEmailMsg("Email Address");
        setEmail(email);
        setEmailCheckFlg(true);
    }else{
        setEmailMsg("E-mail形式で入力してください");
        setEmailCheckFlg(false);
    }
  }

  let passwordOnChange = (e) =>{
    let password = e.target.value;
    //空文字・null・２０文字以内・password形式のバリデーションチェック
    if(password === "" || password === null){
        setPasswordMsg("Password");
        setPasswordCheckFlg(false);
    }else if(password.length <= 20 && passwordReg.test(password)){
        setPasswordMsg("Password");
        setPassword(password);
        setPasswordCheckFlg(true);
    }else{
        setPasswordMsg("半角英数字8〜20文字で入力してください");
        setPasswordCheckFlg(false);
    }
  }

  //API呼び出し→アカウント作成→画面遷移
  let check = () =>{
    const userInput = {email: email,password: password};
    const method = "POST";
    const body = JSON.stringify(userInput);
    const headers = {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    };
    fetch("/accountRegisterAPI", {method, headers, body})
      .then(res => res.json())
      .then(data => {
        setApiMsg(data.message);
        if(data.result){
			//結果がtrueならJSONからIDとEmail情報を保持しながら、プロフィール作成画面に遷移させるという処理を入れたいが、一旦今日はここまで。
        }
      })
      .catch(console.error);
  }

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          アカウント作成
        </Typography>
          <Grid container spacing={2}>
            <Typography variant="body2" color="error" align="center">
              {apiMsg}
            </Typography>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label={emailMsg}
                name="email"
                autoComplete="email"
                inputProps={{ maxLength: 50 }}
                onChange={e => emailOnChange(e)}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label={passwordMsg}
                type="text"
                id="password"
                autoComplete="current-password"
                inputProps={{ maxLength: 20 }}
                onChange={e => passwordOnChange(e)}
              />
            </Grid>
          </Grid>
          <Button
            type="button"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            disabled={disabledFlg}
            onClick = {() => check()}
          >
            Sign Up
          </Button>
          <Grid container justify="flex-end">
            <Grid item>
              <Link href="/login" variant="body2">
                アカウントをすでにお持ちの場合：ログイン
              </Link>
            </Grid>
          </Grid>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Container>
  );
}
